import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios';

const StripeCheckoutButton = ({ price }) => {
  const priceForStripe = price * 100;
  const publishableKey =
    'pk_test_51HeLkJA5uhCwDQ1QXBM7enoh9XMmBaH1BC0UScpFRgK7KPkp3HisyeutiY8RGEa6gdZV7SWBkNEOjp5Yr3u2wSn9008CVJb35r';

  const onToken = (token) => {
    axios({
      url: 'payment',
      method: 'post',
      data: {
        amount: priceForStripe,
        token: token,
      },
    })
      .then((response) => {
        alert('Payment successful');
      })
      .catch((error) => {
        console.log('Payment error: ', JSON.parse(error));
        alert(
          'There is an issue with your payment. Please make sure that you use the provided credit car number'
        );
      });
  };
  return (
    <StripeCheckout
      label="Pay Now"
      name="e-store-with-react"
      billingAddress
      shippingAddress
      description={`Your total is $${price}`}
      amount={priceForStripe}
      panelLabel="Pay Now"
      token={onToken}
      stripeKey={publishableKey}
    />
  );
};

export default StripeCheckoutButton;
